#!/bin/bash

gh issue create --title "[Mini Project] BMI Calculator" --body "Build a command-line program that: - Takes input: name, weight (kg), height (m) - Calculates BMI = weight / height² - Outputs BMI and category:   - Underweight (<18.5)   - Normal (18.5–24.9)   - Overweight (25–29.9)   - Obese (30+)" --label "mini project,lecture 2"
gh issue create --title "[Mini Project] FizzBuzz" --body "Print numbers from 1 to n, replacing: - Multiples of 3 with "Fizz" - Multiples of 5 with "Buzz" - Multiples of both with "FizzBuzz"." --label "mini project,lecture 2"
gh issue create --title "[Mini Project] Palindrome Checker" --body "Write a program that checks if an input (number or string) is a palindrome. Example: "madam", 121 → Palindrome ✅" --label "mini project,lecture 2"
gh issue create --title "[Mini Project] GCD Finder" --body "Take two numbers as input and find their Greatest Common Divisor using a loop. Use Euclidean algorithm (no recursion)." --label "mini project,lecture 2"
